
var OpenSearchPlugins = (function OpenSearchPlugins() {

'use strict';

var lastPositionRequest = 0;
var lastPosition;

var defaults = {

'Yelp': {
    'shortname': 'Yelp',
    'icon': '',
    'description': 'Yelp opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'http://www.yelp.com/search?find_desc={searchTerms}&src=firefox&find_loc={geo:name}'
    },
    'suggestions': {
      'includeSelf': false,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://54.241.22.16/yelp',
      'parameters': {
        'q': '{searchTerms}',
        'lat': '{geo:lat}',
        'lon': '{geo:lon}'
      }
    }
  },

'Marketplace': {
    'shortname': 'Mozilla Marketplace',
    'icon': '',
    'description': 'Marketplace opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'https://marketplace.firefox.com/search?q={searchTerms}'
    },
    'suggestions': {
      'includeSelf': false,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://54.241.22.16/marketplace',
      'parameters': {
        'q': '{searchTerms}'
      }
    }
  },

'EverythingMe': {
    'shortname': 'Everything.Me',
    'icon': '',
    'description': 'Everything.Me opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'http://54.241.22.16/everythingme/?q={searchTerms}'
    },
    'suggestions': {
      'includeSelf': false,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://54.241.22.16/everythingme',
      'parameters': {
        'q': '{searchTerms}'
      }
    }
  },

  'Bing': {
    'shortname': 'Bing',
    'icon': 'data:image/x-icon;base64,',
    'description': 'Bing opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'http://www.bing.com/search?q={searchTerms}&FORM=MO0001'
    },
    'suggestions': {
      'includeSelf': true,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://api.bing.com/osjson.aspx',
      'parameters': {
        'query': '{searchTerms}',
        'form': 'OSDJAS'
      }
    }
  },

  'Wikipedia': {
    'shortname': 'Wikipedia',
    'icon': 'data:image/x-icon;base64,',
    'description': 'Wikipedia opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'http://en.wikipedia.org/wiki/Special:Search?search={searchTerms}'
    },

    'suggestions': {
      'includeSelf': true,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://en.wikipedia.org/w/api.php',
      'parameters': {
        'action': 'opensearch',
        'search': '{searchTerms}'
      }
    }
  },

 'DuckDuckGo': {
    'shortname': 'DuckDuckGo',
    'icon': 'data:image/x-icon;base64,',
    'description': 'DuckDuckGo opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'https://duckduckgo.com/?q={searchTerms}'
    },

   'suggestions': {
      'includeSelf': true,
      'method': 'get',
      'type': 'application/x-suggestions+json',
      'template': 'http://api.bing.com/osjson.aspx',
      'parameters': {
        'query': '{searchTerms}',
        'form': 'OSDJAS'
      }
    }
  },

 'Google': {
    'shortname': 'Google',
    'icon': 'data:image/x-icon;base64,',
    'description': 'Google opensearch plugin',
    'encoding': 'UTF-8',
    'url': {
      'method': 'get',
      'type': 'text/html',
      'template': 'http://www.google.com/search?q={searchTerms}&hl=en'
    },

   'suggestions': {
      'includeSelf': true,
      'method': 'get',
      'type': 'application/x-suggestions+xml',
      'template': 'http://google.com/complete/search',
      'parameters': {
        'output': 'toolbar',
        'q': '{searchTerms}'
      }
    }
  }
};

function debug(str) {
  console.log('OpenSearchPlugins: ' + str + '\n');
}

var OpenSearch = {
  plugins: defaults,

  getSuggestions: function(name, search, count, callback) {

    var plugin = this.plugins[name];
    if (!plugin || !plugin.suggestions) {
      debug('Can\'t find a plugin or suggestions for ' + name);
    }

    var suggestions = plugin.suggestions;
    var uri = suggestions.template.replace('{searchTerms}', search);

    // Apply search params if any.
    var params = '';
    var parameters = suggestions.parameters;
    for (var param in parameters) {
      if (params) {
        params += '&';
      }

      params += param + '=' + parameters[param].replace(
        '{searchTerms}', search);
    }

    if (params) {
      uri += '?' + params;
    }

    var type = suggestions.type;


    if (uri.indexOf('{geo:lat}') !== -1 || uri.indexOf('{geo:lon}') !== -1) {

      // If we have a location within the last hour or so, use that
      if (Date.now() > (lastPositionRequest + 60*60*1000)) {
        lastPositionRequest = Date.now();
        navigator.geolocation.getCurrentPosition(function(position) {
          lastPosition = [position.coords.latitude, position.coords.longitude];
          uri = uri.replace('{geo:lat}', lastPosition[0]);
          uri = uri.replace('{geo:lon}', lastPosition[1]);
          doXhr();
        });
      } else {
        // If we don't have a last position yet, we are in the process of searching, so wait
        if (!lastPosition) {
          return;
        }
        uri = uri.replace('{geo:lat}', lastPosition[0]);
        uri = uri.replace('{geo:lon}', lastPosition[1]);
        doXhr();
      }
      
    } else {
      doXhr();
    }

    function doXhr(position) {
      var xhr = new XMLHttpRequest({mozSystem: true, mozAnon: true});
      xhr.open('GET', uri, true);
      xhr.responseType = type;
      xhr.onload = function() {
        switch (type) {
          case 'application/x-suggestions+json':
            debug('uri: ' + uri);
            debug('load: ' + xhr.responseText);
            var json = JSON.parse(xhr.responseText);

            var results = [];
            var baseURI = plugin.url.template;
            var keywords = json[1];

            var urls = json[2] || [];
            var images = json[3] || [];

            var limit = Math.min(count || keywords.length);
            for (var i = 0; i < limit; i++) {
              var uri = baseURI.replace('{searchTerms}', keywords[i]);
              var thisResult = {
                title: keywords[i],
                uri: uri
              };

              if (urls[i]) {
                thisResult.uri = urls[i];
              }

              if (images[i]) {
                thisResult.icon = images[i];
              }

              results.push(thisResult);
            }
            break;

          case 'application/x-suggestions+xml':
            var parser = new DOMParser();
            var responseXML = parser.parseFromString(
              xhr.responseText,
              'text/xml'
            );

            var snapshot = responseXML.evaluate(
              '//suggestion/@data',
              responseXML,
              null,
              XPathResult.ORDERED_NODE_SNAPSHOT_TYPE,
              null
            );

            var results = [];
            var baseURI = plugin.url.template;

            for (var i = 0; i < snapshot.snapshotLength; i++) {
              if (i >= count) {
                break;
              }

              var words = snapshot.snapshotItem(i).nodeValue;
              var uri = baseURI.replace('{searchTerms}', words);
              results.push({ 'title': words, 'uri': uri});
            }

            break;

          default:
            debug('Unsupported type: ' + type);
            break;
        }

        // Insert the query into the response if it's not there
        // We might want to control this with a parameter
        var found = false;
        for (var i = 0, result; result = results[i]; i++) {
          if (result.title &&
            result.title.toLowerCase() === search.toLowerCase()) {
            found = true;
            break;
          }
        }
        if (!found && suggestions.includeSelf) {
          var uri = baseURI.replace('{searchTerms}', search);
          results.push({ 'title': search, 'uri': uri});
        }

        callback(results);
      };

      xhr.onerror = function() {
        debug('error: ' + xhr.status);
      };

      xhr.send();
    }
  }
};

return OpenSearch;
})();
